params.fragmentShader:
uniform float u_time;
uniform vec2 u_resolution;
uniform float u_param1;
uniform float u_param2;
uniform float u_param3;
uniform float u_param4;
uniform sampler2D u_spectrum;

float hash(vec2 p) {
    return fract(sin(dot(p, vec2(127.1, 311.7))) * 43758.5453);
}

void main() {
    vec2 uv = gl_FragCoord.xy / u_resolution;
    vec2 p = uv * 2.0 - 1.0;
    p.x *= u_resolution.x / u_resolution.y;

    float sub = u_param1;
    float low = u_param2;
    float mid = u_param3;
    float pres = u_param4;

    vec3 col = vec3(0.0);

    for (int layer = 0; layer < 4; layer++) {
        float fl = float(layer);
        float depth = 1.0 + fl * 0.5;
        float speed = (0.5 + fl * 0.3) * (1.0 + sub * 2.0);

        vec2 grid = p * (20.0 + fl * 10.0);
        grid.y += u_time * speed;
        grid.x += sin(u_time * 0.5 + fl) * low * 2.0;

        vec2 cell = floor(grid);
        vec2 local = fract(grid) - 0.5;

        float rnd = hash(cell + fl * 100.0);
        vec2 offset = vec2(
            hash(cell + vec2(1.0, 0.0) + fl * 100.0) - 0.5,
            hash(cell + vec2(0.0, 1.0) + fl * 100.0) - 0.5
        ) * 0.3;

        float d = length(local - offset);
        float size = 0.02 + rnd * 0.03;
        size *= 1.0 + mid * 2.0;

        float star = size / (d * d + size);
        star *= step(0.3, rnd);

        vec3 layerCol;
        if (layer == 0) layerCol = vec3(0.5, 0.7, 1.0);
        else if (layer == 1) layerCol = vec3(1.0, 0.5, 0.7);
        else if (layer == 2) layerCol = vec3(0.7, 1.0, 0.5);
        else layerCol = vec3(1.0, 0.9, 0.5);

        float bandReact;
        if (layer == 0) bandReact = sub;
        else if (layer == 1) bandReact = low;
        else if (layer == 2) bandReact = mid;
        else bandReact = pres;
        star *= 0.3 + bandReact * 3.0;

        col += layerCol * star * 0.05 / depth;
    }

    float spec = texture2D(u_spectrum, vec2(uv.x, 0.0)).r;
    col += vec3(0.1, 0.05, 0.2) * spec * (1.0 - uv.y) * 0.5;

    gl_FragColor = vec4(col, 1.0);
}
